import React from 'react';

const RequestStatus = {
  PENDING: 'PENDING',
  APPROVED: 'APPROVED',
  DECLINED: 'DECLINED'
};

const Badge = ({ status }) => {
  const styles = {
    [RequestStatus.PENDING]: 'bg-amber-100 text-amber-700 border-amber-200',
    [RequestStatus.APPROVED]: 'bg-emerald-100 text-emerald-700 border-emerald-200',
    [RequestStatus.DECLINED]: 'bg-rose-100 text-rose-700 border-rose-200',
  };

  return (
    <span className={`px-2.5 py-0.5 rounded-full text-[10px] font-black uppercase tracking-wider border ${styles[status]}`}>
      {status}
    </span>
  );
};

export default Badge;
