// Service categories mapping
export const SERVICE_CATEGORIES = {
  plumbing: 'Service Plumbing',
  electrical: 'Service Electrical',
  carpentry: 'Service Carpentry',
  cleaning: 'Service Cleaning',
  painting: 'Service Painting',
  gardening: 'Service Gardening',
  mechanic: 'Service Mechanic',
  tutoring: 'Service Tutoring'
};

// Service areas mapping
export const SERVICE_AREAS = {
  kathmandu: 'Kathmandu',
  bhaktapur: 'Bhaktapur',
  lalitpur: 'Lalitpur',
  pokhara: 'Pokhara'
};

// Application status colors
export const STATUS_COLORS = {
  pending: 'bg-yellow-100 text-yellow-800',
  verified: 'bg-green-100 text-green-800',
  approved: 'bg-green-100 text-green-800',
  rejected: 'bg-red-100 text-red-800'
};

// API endpoints
export const API_BASE_URL = 'http://localhost:5000/api';
