import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Menu, X, Search, Star, MapPin, ArrowRight, Check } from 'lucide-react';
import './HomePage.css'; // Note: lowercase 'p'

const HomePage = () => {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();

  const navItems = [
    { name: 'Services', path: '/services' },
    { name: 'Find Jobs', path: '/jobs' },
    { name: 'Providers', path: '/providers' },
    { name: 'Contact', path: '/contact' },
  ];

  const services = [
    { name: 'Construction', icon: 'ðŸ”¨', desc: 'Professional builders' },
    { name: 'Cleaning', icon: 'ðŸ§¹', desc: 'Cleaning services' },
    { name: 'Automotive', icon: 'ðŸš—', desc: 'Car maintenance' },
    { name: 'Healthcare', icon: 'âš•ï¸', desc: 'Medical services' },
    { name: 'IT Support', icon: 'ðŸ’»', desc: 'Tech solutions' },
    { name: 'Plumbing', icon: 'ðŸ”§', desc: 'Plumbing experts' },
    { name: 'Electrical', icon: 'âš¡', desc: 'Electrical work' },
    { name: 'Tutoring', icon: 'ðŸ“š', desc: 'Education services' },
  ];

  const providers = [
    {
      name: 'John Smith',
      title: 'Construction Expert',
      location: 'New York, NY',
      rating: 4.8,
      reviews: 342,
      verified: true,
    },
    {
      name: 'City Builders Inc',
      title: 'General Contracting',
      location: 'Chicago, IL',
      rating: 4.9,
      reviews: 218,
      verified: true,
    },
    {
      name: 'Quick Clean Services',
      title: 'Professional Cleaning',
      location: 'Los Angeles, CA',
      rating: 4.7,
      reviews: 456,
      verified: true,
    },
    {
      name: 'Auto Care Masters',
      title: 'Vehicle Maintenance',
      location: 'Houston, TX',
      rating: 4.6,
      reviews: 189,
      verified: true,
    },
  ];

  const stats = [
    { label: '50K+', desc: 'Jobs Posted' },
    { label: '10K+', desc: 'Service Providers' },
    { label: '500+', desc: 'Companies' },
  ];

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      console.log('Searching for:', searchQuery);
      // In a real app, you would implement search functionality
      alert(`Searching for: ${searchQuery}`);
    }
  };

  const handleLogin = () => {
    console.log('Login clicked');
    // Navigate to login page if you have one
    // navigate('/login');
    alert('Login functionality would go here');
  };

  const handleSignUp = () => {
    navigate('/signup');
  };

  const handleViewAllServices = () => {
    console.log('View all services clicked');
    // navigate('/services');
  };

  const handleViewAllProviders = () => {
    console.log('View all providers clicked');
    // navigate('/providers');
  };

  const Button = ({ children, variant = 'primary', size = 'medium', className = '', ...props }) => {
    const baseClasses = 'font-medium transition-all duration-200 cursor-pointer disabled:opacity-50';
    const sizeClasses = {
      small: 'px-3 py-1.5 text-sm rounded-md',
      medium: 'px-4 py-2 rounded-lg',
      large: 'px-6 py-3 text-lg rounded-lg',
    };
    const variantClasses = {
      primary: 'bg-gradient-to-r from-blue-600 to-indigo-600 text-white hover:opacity-90',
      secondary: 'bg-white text-blue-600 border border-blue-600 hover:bg-blue-50',
      ghost: 'bg-transparent text-gray-600 hover:bg-gray-100',
      outline: 'bg-transparent border border-gray-300 text-gray-700 hover:border-gray-400',
    };

    return (
      <button
        className={`${baseClasses} ${sizeClasses[size]} ${variantClasses[variant]} ${className}`}
        {...props}
      >
        {children}
      </button>
    );
  };

  return (
    <div className="home-container">
      {/* Navigation */}
      <nav className="navbar">
        <div className="nav-container">
          {/* Logo */}
          <Link to="/" className="logo-link">
            <div className="logo">
              <div className="logo-icon">K</div>
              <span className="logo-text">Kamau Nepal</span>
            </div>
          </Link>

          {/* Desktop Navigation */}
          <div className="desktop-nav">
            {navItems.map((item, idx) => (
              <Link key={idx} to={item.path} className="nav-item">
                {item.name}
              </Link>
            ))}
          </div>

          {/* Auth Buttons */}
          <div className="auth-buttons">
            <Button variant="ghost" size="small" onClick={handleLogin}>
              Login
            </Button>
            <Button size="small" onClick={handleSignUp}>
              Sign Up
            </Button>
          </div>

          {/* Mobile Menu Button */}
          <button 
            className="mobile-menu-btn" 
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
            aria-label={mobileMenuOpen ? 'Close menu' : 'Open menu'}
          >
            {mobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>

        {/* Mobile Menu */}
        {mobileMenuOpen && (
          <div className="mobile-menu">
            {navItems.map((item, idx) => (
              <Link 
                key={idx} 
                to={item.path} 
                className="mobile-nav-item" 
                onClick={() => setMobileMenuOpen(false)}
              >
                {item.name}
              </Link>
            ))}
            <div className="mobile-auth">
              <Button variant="outline" size="small" onClick={handleLogin}>
                Login
              </Button>
              <Button size="small" onClick={handleSignUp}>
                Sign Up
              </Button>
            </div>
          </div>
        )}
      </nav>

      {/* Hero Section */}
      <section className="hero">
        <div className="hero-content">
          <div className="badge">
            <span>âœ¨ Connecting talent with opportunity</span>
          </div>
          <h1>Find Skilled Professionals Near You</h1>
          <p className="hero-description">
            Discover and hire service providers or find your next great opportunity. Connect with verified
            professionals across hundreds of services.
          </p>

          {/* Search Bar */}
          <div className="search-section">
            <form onSubmit={handleSearch} className="search-form">
              <div className="search-input-container">
                <Search className="search-icon" />
                <input
                  type="text"
                  placeholder="Search for services, skills, or providers..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="search-input"
                />
              </div>
              <Button type="submit">Search</Button>
            </form>
            <p className="search-tip">Try: "Plumber", "Web Developer", "House Cleaning"</p>
          </div>

          {/* Stats */}
          <div className="stats-grid">
            {stats.map((stat, idx) => (
              <div key={idx} className="stat-item">
                <div className="stat-number">{stat.label}</div>
                <div className="stat-label">{stat.desc}</div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Services Section */}
      <section id="services" className="section services-section">
        <div className="section-header">
          <div className="section-title-group">
            <h2>Popular Services</h2>
            <p className="section-subtitle">Explore our wide range of professional services</p>
          </div>
          <button className="view-all-link" onClick={handleViewAllServices}>
            View All <ArrowRight size={18} />
          </button>
        </div>

        <div className="services-grid">
          {services.map((service, idx) => (
            <button 
              key={idx} 
              className="service-card"
              onClick={() => console.log(`Selected ${service.name}`)}
            >
              <div className="service-icon">{service.icon}</div>
              <h3>{service.name}</h3>
              <p>{service.desc}</p>
            </button>
          ))}
        </div>
      </section>

      {/* CTA Section */}
      <section className="cta-section">
        <div className="cta-content">
          <h2>Ready to Get Started?</h2>
          <p>Join thousands of professionals and businesses already using our platform</p>
          <div className="cta-buttons">
            <Button variant="secondary" size="large" onClick={() => navigate('/jobs')}>
              Find Jobs
            </Button>
            <Button size="large" className="bg-white text-blue-600 hover:bg-blue-50">
              Hire Now
            </Button>
          </div>
        </div>
      </section>

      {/* Providers Section */}
      <section id="providers" className="section providers-section">
        <div className="section-header">
          <div className="section-title-group">
            <h2>Top Service Providers</h2>
            <p className="section-subtitle">Highly rated professionals ready to help</p>
          </div>
          <button className="view-all-link" onClick={handleViewAllProviders}>
            View All <ArrowRight size={18} />
          </button>
        </div>

        <div className="providers-grid">
          {providers.map((provider, idx) => (
            <div key={idx} className="provider-card">
              {/* Avatar */}
              <div className="provider-avatar">{provider.name.charAt(0)}</div>

              {/* Info */}
              <h3>{provider.name}</h3>
              <p className="provider-title">{provider.title}</p>

              {/* Rating */}
              <div className="provider-rating">
                <div className="stars">
                  {[...Array(5)].map((_, i) => (
                    <Star
                      key={i}
                      className={`star ${i < Math.floor(provider.rating) ? 'filled' : ''}`}
                    />
                  ))}
                </div>
                <span className="rating-number">{provider.rating}</span>
                <span className="reviews">({provider.reviews})</span>
              </div>

              {/* Location */}
              <div className="provider-location">
                <MapPin size={16} />
                {provider.location}
              </div>

              {/* Verified Badge */}
              {provider.verified && (
                <div className="verified-badge">
                  <Check size={14} />
                  Verified
                </div>
              )}

              {/* Action Button */}
              <Button className="hire-button">Hire Now</Button>
            </div>
          ))}
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-grid">
            {/* Brand */}
            <div className="footer-brand">
              <div className="footer-logo">
                <div className="logo-icon">K</div>
                <span className="logo-text">Kamau Nepal</span>
              </div>
              <p className="footer-description">Connecting skilled professionals with opportunities.</p>
            </div>

            {/* For Job Seekers */}
            <div className="footer-links">
              <h4>For Job Seekers</h4>
              <ul>
                <li><Link to="/jobs">Find Jobs</Link></li>
                <li><Link to="/companies">Browse Companies</Link></li>
                <li><Link to="/career-advice">Career Advice</Link></li>
              </ul>
            </div>

            {/* For Employers */}
            <div className="footer-links">
              <h4>For Employers</h4>
              <ul>
                <li><Link to="/post-job">Post a Job</Link></li>
                <li><Link to="/candidates">Find Candidates</Link></li>
                <li><Link to="/pricing">Pricing</Link></li>
              </ul>
            </div>

            {/* Company */}
            <div className="footer-links">
              <h4>Company</h4>
              <ul>
                <li><Link to="/about">About Us</Link></li>
                <li><Link to="/blog">Blog</Link></li>
                <li><Link to="/contact">Contact</Link></li>
              </ul>
            </div>
          </div>

          {/* Bottom */}
          <div className="footer-bottom">
            <p>&copy; 2025 Kamau Nepal. All rights reserved.</p>
            <div className="footer-links-bottom">
              <Link to="/privacy">Privacy Policy</Link>
              <Link to="/terms">Terms of Service</Link>
            </div>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default HomePage;
