import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  ArrowLeft, CheckCircle2, User, Mail, Phone, 
  MapPin, Briefcase, Info, Star, ShieldCheck, 
  Camera, Upload, FileText, X, Trash2
} from 'lucide-react';
import { Button } from '../components/Button';
import { SERVICE_CATEGORIES, LOCATIONS } from '../constants';

const ProfessionalRegistration = () => {
  const navigate = useNavigate();
  const fileInputRef = useRef(null);
  const docInputRef = useRef(null);

  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    username: '',
    email: '',
    phone: '',
    serviceCategory: '',
    serviceArea: '',
    hourlyWage: '',
    bio: ''
  });

  const [profileImage, setProfileImage] = useState(null);
  const [documents, setDocuments] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitted, setSubmitted] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setProfileImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleDocChange = (e) => {
    const files = e.target.files;
    if (files) {
      const newDocs = Array.from(files).map((file) => ({
        id: Math.random().toString(36).substr(2, 9),
        name: file.name,
        size: (file.size / 1024).toFixed(1) + ' KB'
      }));
      setDocuments(prev => [...prev, ...newDocs]);
    }
  };

  const removeDoc = (id) => {
    setDocuments(prev => prev.filter(doc => doc.id !== id));
  };

  const triggerImageUpload = () => fileInputRef.current?.click();
  const triggerDocUpload = () => docInputRef.current?.click();

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    // Simulate API call
    setTimeout(() => {
      setIsSubmitting(false);
      setSubmitted(true);
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }, 1500);
  };

  if (submitted) {
    return (
      <div className="min-h-[80vh] flex items-center justify-center p-4">
        <div className="max-w-md w-full text-center p-12 bg-white rounded-[40px] shadow-2xl border border-slate-100">
          <div className="w-24 h-24 bg-teal-100 rounded-full flex items-center justify-center mx-auto mb-8 text-teal-600 animate-bounce">
            <CheckCircle2 size={48} />
          </div>
          <h2 className="text-3xl font-black text-slate-900 mb-4">Registration Complete!</h2>
          <p className="text-slate-500 mb-10 font-medium">
            Your profile and documents have been submitted for review. Verification usually takes 24-48 hours.
          </p>
          <Button variant="primary" className="w-full py-4" onClick={() => navigate('/')}>
            Return to Home
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-slate-50 min-h-screen py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-6xl mx-auto">
        <button 
          onClick={() => navigate(-1)}
          className="group inline-flex items-center gap-2 text-slate-500 hover:text-orange-500 font-bold mb-8 transition-colors"
        >
          <ArrowLeft size={20} className="group-hover:-translate-x-1 transition-transform" />
          Back to previous
        </button>

        <div className="grid grid-cols-1 lg:grid-cols-12 gap-12 items-start">
          {/* Left Side: Info */}
          <div className="lg:col-span-5 space-y-8">
            <div>
              <h1 className="text-4xl lg:text-5xl font-black text-slate-900 leading-tight mb-6">
                Turn your skills into <span className="text-orange-500 underline decoration-teal-400">Income</span>
              </h1>
              <p className="text-lg text-slate-600 font-medium leading-relaxed">
                Join Kamau Nepal's community of certified professionals. We help you find clients, manage your schedule, and grow your local reputation.
              </p>
            </div>

            {/* Profile Picture Upload Preview Card */}
            <div className="bg-white p-6 rounded-[32px] border border-slate-100 shadow-sm flex flex-col items-center">
              <div className="relative group cursor-pointer" onClick={triggerImageUpload}>
                <div className="w-32 h-32 rounded-full overflow-hidden bg-slate-100 border-4 border-white shadow-lg relative">
                  {profileImage ? (
                    <img src={profileImage} alt="Profile Preview" className="w-full h-full object-cover" />
                  ) : (
                    <div className="w-full h-full flex items-center justify-center text-slate-300">
                      <User size={64} />
                    </div>
                  )}
                  <div className="absolute inset-0 bg-black/40 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity">
                    <Camera className="text-white" size={24} />
                  </div>
                </div>
                <button 
                  type="button"
                  className="absolute bottom-1 right-1 bg-orange-500 text-white p-2 rounded-full shadow-md hover:bg-orange-600 transition-colors"
                >
                  <Camera size={16} />
                </button>
              </div>
              <input 
                type="file" 
                ref={fileInputRef} 
                onChange={handleImageChange} 
                className="hidden" 
                accept="image/*" 
              />
              <div className="mt-4 text-center">
                <h3 className="font-bold text-slate-900">Profile Photo</h3>
                <p className="text-xs text-slate-500 mt-1">Clear photos help build trust with clients</p>
              </div>
            </div>

            <div className="space-y-6">
              {[
                { 
                  icon: <Star className="text-orange-500" />, 
                  title: "Build your reputation", 
                  desc: "Get verified and receive ratings from real customers in Kathmandu & Bhaktapur." 
                },
                { 
                  icon: <ShieldCheck className="text-teal-600" />, 
                  title: "Work with trust", 
                  desc: "Our secure platform ensures you connect with legitimate clients and get paid on time." 
                }
              ].map((item, i) => (
                <div key={i} className="flex gap-4 p-5 bg-white rounded-3xl border border-slate-100 shadow-sm">
                  <div className="flex-shrink-0 w-12 h-12 bg-slate-50 rounded-2xl flex items-center justify-center">
                    {item.icon}
                  </div>
                  <div>
                    <h3 className="font-bold text-slate-900 mb-1">{item.title}</h3>
                    <p className="text-sm text-slate-500 font-medium">{item.desc}</p>
                  </div>
                </div>
              ))}
            </div>

            <div className="bg-orange-50 p-8 rounded-[32px] border border-orange-100">
              <div className="flex items-start gap-4">
                <Info className="text-orange-500 flex-shrink-0" size={24} />
                <p className="text-orange-800 text-sm font-bold leading-relaxed">
                  TIP: Uploading professional certifications and IDs speeds up the verification process.
                </p>
              </div>
            </div>
          </div>

          {/* Right Side: Form */}
          <div className="lg:col-span-7">
            <div className="bg-white p-8 lg:p-12 rounded-[40px] shadow-2xl shadow-slate-200/50 border border-slate-100">
              <h2 className="text-2xl font-black text-slate-900 mb-8">Professional Details</h2>
              
              <form onSubmit={handleSubmit} className="space-y-6">
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
                  {/* First Name */}
                  <div className="space-y-2">
                    <label className="text-sm font-bold text-slate-700 ml-1">First Name</label>
                    <div className="relative">
                      <User className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
                      <input 
                        required
                        type="text" 
                        name="firstName"
                        placeholder="Ram"
                        className="w-full bg-slate-50 border border-slate-200 rounded-2xl py-4 pl-12 pr-4 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none transition-all font-medium"
                        value={formData.firstName}
                        onChange={handleInputChange}
                      />
                    </div>
                  </div>
                  {/* Last Name */}
                  <div className="space-y-2">
                    <label className="text-sm font-bold text-slate-700 ml-1">Last Name</label>
                    <div className="relative">
                      <User className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
                      <input 
                        required
                        type="text" 
                        name="lastName"
                        placeholder="Bahadur"
                        className="w-full bg-slate-50 border border-slate-200 rounded-2xl py-4 pl-12 pr-4 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none transition-all font-medium"
                        value={formData.lastName}
                        onChange={handleInputChange}
                      />
                    </div>
                  </div>
                </div>

                {/* Username */}
                <div className="space-y-2">
                  <label className="text-sm font-bold text-slate-700 ml-1">Username</label>
                  <div className="relative">
                    <span className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 font-bold">@</span>
                    <input 
                      required
                      type="text" 
                      name="username"
                      placeholder="ram_pro_2025"
                      className="w-full bg-slate-50 border border-slate-200 rounded-2xl py-4 pl-10 pr-4 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none transition-all font-medium"
                      value={formData.username}
                      onChange={handleInputChange}
                    />
                  </div>
                </div>

                <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
                  {/* Email */}
                  <div className="space-y-2">
                    <label className="text-sm font-bold text-slate-700 ml-1">Email Address</label>
                    <div className="relative">
                      <Mail className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
                      <input 
                        required
                        type="email" 
                        name="email"
                        placeholder="ram@example.com"
                        className="w-full bg-slate-50 border border-slate-200 rounded-2xl py-4 pl-12 pr-4 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none transition-all font-medium"
                        value={formData.email}
                        onChange={handleInputChange}
                      />
                    </div>
                  </div>
                  {/* Phone */}
                  <div className="space-y-2">
                    <label className="text-sm font-bold text-slate-700 ml-1">Phone Number</label>
                    <div className="relative">
                      <Phone className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
                      <input 
                        required
                        type="tel" 
                        name="phone"
                        placeholder="98XXXXXXXX"
                        className="w-full bg-slate-50 border border-slate-200 rounded-2xl py-4 pl-12 pr-4 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none transition-all font-medium"
                        value={formData.phone}
                        onChange={handleInputChange}
                      />
                    </div>
                  </div>
                </div>

                <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
                  {/* Service Category */}
                  <div className="space-y-2">
                    <label className="text-sm font-bold text-slate-700 ml-1">Service Category</label>
                    <div className="relative">
                      <Briefcase className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
                      <select 
                        required
                        name="serviceCategory"
                        className="w-full bg-slate-50 border border-slate-200 rounded-2xl py-4 pl-12 pr-4 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none transition-all font-medium appearance-none"
                        value={formData.serviceCategory}
                        onChange={handleInputChange}
                      >
                        <option value="">Select category</option>
                        {SERVICE_CATEGORIES.map(cat => (
                          <option key={cat.id} value={cat.id}>{cat.icon} {cat.name}</option>
                        ))}
                      </select>
                    </div>
                  </div>
                  {/* Service Area */}
                  <div className="space-y-2">
                    <label className="text-sm font-bold text-slate-700 ml-1">Service Area</label>
                    <div className="relative">
                      <MapPin className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
                      <select 
                        required
                        name="serviceArea"
                        className="w-full bg-slate-50 border border-slate-200 rounded-2xl py-4 pl-12 pr-4 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none transition-all font-medium appearance-none"
                        value={formData.serviceArea}
                        onChange={handleInputChange}
                      >
                        <option value="">Select location</option>
                        <optgroup label="Kathmandu">
                          {LOCATIONS.filter(l => l.city === 'Kathmandu').map(loc => (
                            <option key={loc.id} value={loc.area}>{loc.area}</option>
                          ))}
                        </optgroup>
                        <optgroup label="Bhaktapur">
                          {LOCATIONS.filter(l => l.city === 'Bhaktapur').map(loc => (
                            <option key={loc.id} value={loc.area}>{loc.area}</option>
                          ))}
                        </optgroup>
                      </select>
                    </div>
                  </div>
                </div>

                {/* Hourly Wage */}
                <div className="space-y-2">
                  <label className="text-sm font-bold text-slate-700 ml-1">Hourly Wage ()</label>
                  <div className="relative">
                    <div className="absolute left-4 top-1/2 -translate-y-1/2 font-bold text-slate-400"></div>
                    <input 
                      required
                      type="number" 
                      name="hourlyWage"
                      placeholder="e.g. 500"
                      className="w-full bg-slate-50 border border-slate-200 rounded-2xl py-4 pl-12 pr-4 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none transition-all font-medium"
                      value={formData.hourlyWage}
                      onChange={handleInputChange}
                    />
                  </div>
                </div>

                {/* Bio */}
                <div className="space-y-2">
                  <label className="text-sm font-bold text-slate-700 ml-1">About Your Service</label>
                  <textarea 
                    name="bio"
                    placeholder="Tell potential clients about your experience, tools, and expertise..."
                    className="w-full bg-slate-50 border border-slate-200 rounded-2xl py-4 px-6 h-32 focus:ring-2 focus:ring-orange-500 focus:border-transparent outline-none transition-all font-medium"
                    value={formData.bio}
                    onChange={handleInputChange}
                  />
                </div>

                {/* Verification Documents Section */}
                <div className="space-y-4 pt-4">
                  <div className="flex justify-between items-center">
                    <label className="text-sm font-bold text-slate-700 ml-1">Verification Documents</label>
                    <button 
                      type="button" 
                      onClick={triggerDocUpload}
                      className="text-sm font-bold text-teal-600 hover:text-teal-700 flex items-center gap-1 transition-colors"
                    >
                      <Upload size={16} /> Add Documents
                    </button>
                  </div>
                  
                  <input 
                    type="file" 
                    ref={docInputRef} 
                    onChange={handleDocChange} 
                    className="hidden" 
                    multiple
                    accept=".pdf,.jpg,.jpeg,.png"
                  />

                  {documents.length > 0 ? (
                    <div className="grid grid-cols-1 gap-3">
                      {documents.map((doc) => (
                        <div key={doc.id} className="flex items-center justify-between p-4 bg-slate-50 border border-slate-200 rounded-2xl group hover:border-teal-200 transition-colors">
                          <div className="flex items-center gap-3">
                            <div className="w-10 h-10 bg-teal-100 rounded-xl flex items-center justify-center text-teal-600">
                              <FileText size={20} />
                            </div>
                            <div>
                              <p className="text-sm font-bold text-slate-800 line-clamp-1">{doc.name}</p>
                              <p className="text-xs text-slate-400 font-medium">{doc.size}</p>
                            </div>
                          </div>
                          <button 
                            type="button" 
                            onClick={() => removeDoc(doc.id)}
                            className="p-2 text-slate-400 hover:text-red-500 transition-colors"
                          >
                            <Trash2 size={18} />
                          </button>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div 
                      onClick={triggerDocUpload}
                      className="border-2 border-dashed border-slate-200 rounded-[32px] p-8 text-center cursor-pointer hover:border-teal-400 hover:bg-teal-50/30 transition-all group"
                    >
                      <div className="w-16 h-16 bg-slate-100 rounded-2xl flex items-center justify-center mx-auto mb-4 text-slate-400 group-hover:bg-teal-100 group-hover:text-teal-600 transition-colors">
                        <Upload size={24} />
                      </div>
                      <p className="font-bold text-slate-700 mb-1">Upload Certificates or ID</p>
                      <p className="text-xs text-slate-400 font-medium leading-relaxed max-w-[200px] mx-auto">
                        PDF, JPG or PNG (Max 5MB each). We keep your documents secure.
                      </p>
                    </div>
                  )}
                </div>

                <div className="pt-8">
                  <Button 
                    type="submit" 
                    variant="primary" 
                    className="w-full py-5 rounded-2xl text-lg shadow-xl shadow-orange-500/20"
                    disabled={isSubmitting}
                  >
                    {isSubmitting ? "Submitting Registration..." : "Complete Registration"}
                  </Button>
                  <p className="text-center text-xs text-slate-400 mt-6 font-medium">
                    By clicking "Complete Registration", you agree to our <a href="#" className="underline hover:text-orange-500">Terms of Service</a> and <a href="#" className="underline hover:text-orange-500">Privacy Policy</a>.
                  </p>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfessionalRegistration;
